import { useState } from 'react'
import './LoginSignup.css'
import "../asset/cover.jpg"
import name from "../asset/user.png"
import email from "../asset/email.png"
import password from "../asset/password.png"
const LoginSignup = () => {
    const [action, setAction] = useState("Sign up")
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');



    const handleUsernameChange = (e) => {
        setUsername(e.target.value);
      };

      const handleEmailChange = (e) => {
        setEmail(e.target.value);
      };

      const handlePasswordChange = (e) => {
        setPassword(e.target.value);
      };

      const handleSubmit = async (e) => {
        e.preventDefault();

        // Prepare data for API call
        const userData = {
          username,
          email,
          password,
        };

        try {
            // Make an HTTP call to your API
            // const response = await axios.post('YOUR_API_ENDPOINT', userData);

            // Handle the API response as needed
            // console.log(response.data);
          } catch (error) {
            // Handle errors
            console.error('Error signing up:', error);
          }
        };






    return (
        // <form onSubmit={handleSubmit}>
        <div className='container-back'>

            <div className='container'>

                <div className='header'>
                    <div className='text'>{action}</div>
                    <div className='underline'></div>
                </div>
                <div className='inputs'>
                    {action === "Login" ? <div></div> : <div className='input'>
                        <img src={name} width={"50px"} alt="name" />
                        <input type="text" placeholder='Name' />
                    </div>}


                    <div className='input'>
                        <img src={email} width={"50px"} alt="" />
                        <input type="email" placeholder='Email' />
                    </div>

                    <div className='input'>
                        <img src={password} width={"50px"} alt="" />
                        <input type="password" placeholder='Password' />
                    </div>
                </div>
                <div className='submit-container'>
                    <div className={action === "Login" ? "submit gray" : "submit"} onClick={() => (setAction("Sign up"))}>Sign Up</div>
                    <div className={action === "Sign up" ? "submit gray" : "submit"} onClick={() => (setAction("Login"))}>Login</div>

                </div>



            </div>

        </div>
        // </form>
    )
}

export default LoginSignup
