// About.js
import React from 'react';

const About = () => {
  return (
    <div className="about-container">
    <h2>About PicMorphy App</h2>
    <p>
      Welcome to PicMorphy, a revolutionary app that transforms plain text into
      captivating images. With PicMorphy, you can turn your words into visually
      stunning graphics, adding a creative touch to your messages.
    </p>
    <p>
      PicMorphy offers a user-friendly interface for effortlessly creating beautiful
      visuals. Whether you're crafting social media posts, sharing inspirational quotes,
      or generating unique artwork, PicMorphy empowers you to express yourself with
      images that convey the essence of your text.
    </p>
  </div>
  );
};

export default About;
